import React from 'react';
import Expenses from './Expenses';
import './ExpensesFilter.css';

const ExpensesFilter = (props) => {
    const sendYear = (event) =>{
        props.year(event.taget.value);
        console.log(event.taget.value)
    }
  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option value='2022' onSelect={sendYear}>2022</option>
          <option value='2021' onSelect={sendYear}>2021</option>
          <option value='2020' onSelect={sendYear}>2020</option>
          <option value='2019' onSelect={sendYear}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
